// Hook 使用
import React, { useState, useEffect } from 'react';

function Counter() {
  const [count, setCount] = useState(0);

  useEffect(() =&gt; {
    document.title = `点击了 ${count} 次`;
  }, [count]);

  return (
    &lt;div&gt;
      &lt;p&gt;你点击了 {count} 次&lt;/p&gt;
      &lt;button onClick={() =&gt; setCount(count + 1)}&gt;
        点击我
      &lt;/button&gt;
    &lt;/div&gt;
  );
}
